    function moveRight() {
        curMainTick < main_slides.length - 1 ? curMainTick++ : curMainTick = 0, clearTimeout(sliderTimer), position = -window.innerWidth * curMainTick + "px", main_slides_container.css("transform", "translateX(" + position + ")"), sliderTimer = setTimeout(moveRight, 5e3)
    }
    document.addEventListener("scroll", function() {
        var i = $(".main__text"),
                n = $(".main__kids"),
                e = $(".section-main"),
                r = window.pageYOffset;
        window.pageYOffset >= e.height() / 5 || (i.css("margin-top", r / 2.5 + "px"), n.css("margin-top", r / 4 + "px"))
    });
    var main_slides = $(".main__slider"),
            main_slides_container = $(".main__slider-container"),
            flaggg = !0,
            mainArrowLeft = $(".main__arrow-left"),
            mainArrowRight = $(".main__arrow-right");
    main_slides_container.css("width", 100 * main_slides.length + "%");
    var curMainTick = 0,
            position, sliderTimer;
    mainArrowRight.click(moveRight), mainArrowLeft.click(function() {
        curMainTick > 0 ? curMainTick-- : curMainTick = main_slides.length - 1, clearTimeout(sliderTimer), position = -window.innerWidth * curMainTick + "px", main_slides_container.css("transform", "translateX(" + position + ")"), sliderTimer = setTimeout(moveRight, 5e3)
    }), sliderTimer = setTimeout(moveRight, 5e3);

    var studioShowed = !1;
    document.addEventListener("scroll", function() {
        if (!studioShowed) {
            var t = window.pageYOffset + window.innerHeight,
                    i = $(".studio__icons"),
                    o = $(".studio__icon");
            t >= i.offset().top && (studioShowed = !0, o.each(function(t) {
                setTimeout(function() {
                    $(this).addClass("show")
