            }), i.addClass("show"))
        }
    }), $(".studio__youtube-btn").click(function(t) {
        $(this).fadeOut(), $(this).parent().find(".studio__youtube").fadeIn()
    });
    var overlay = $(".team__overlay"),
            guys = $(".team__guy");
    document.addEventListener("scroll", function() {
        var a = $(".team__logo"),
                s = $(".team"),
                e = s.offset().top,
                m = window.pageYOffset;
        m + window.innerHeight >= e && m <= e + s.height() && a.css("background-position", "center " + (m - e) / 10 + "px")
