        guys.addClass("blur"), overlay.addClass("show"), $(this).hasClass("team__man1") && ($(".team__man1").removeClass("blur"), $(".team__man1").addClass("hover")), $(this).hasClass("team__man2") && ($(".team__man2").removeClass("blur"), $(".team__man2").addClass("hover")), $(this).hasClass("team__man3") && ($(".team__man3").removeClass("blur"), $(".team__man3").addClass("hover")), $(this).hasClass("team__man4") && ($(".team__man4").removeClass("blur"), $(".team__man4").addClass("hover"))
    }), guys.mouseleave(function() {
        guys.removeClass("hover"), guys.removeClass("blur"), overlay.removeClass("show")
    });

    var partners_slides = $(".partners__slide"),
