            partnersArrowLeft = $(".partners__arrow-left"),
            partnersArrowRight = $(".partners__arrow-right");
    partners_slides_container.css("width", oneTick * partners_slides_img.length + "px");
    var position2, curTick = 0;
    partnersArrowRight.click(function() {
        var e = $(".partners__slider img").width() + 50;
        curTick < 2 && (curTick++, position2 = -e * curTick + "px", partners_slides_container.css("transform", "translateX(" + position2 + ")"))
    }), partnersArrowLeft.click(function() {
        var e = $(".partners__slider img").width() + 50;
        curTick > 0 && (curTick--, position2 = -e * curTick + "px", partners_slides_container.css("transform", "translateX(" + position2 + ")"))
    });
    var tutorialModal = new Modal({
                el: document.getElementById("agreement")
            }),
            dialog = $(".modal-dialog");
    $(".tutorial__button").click(function() {
        tutorialModal.show();
        var e = (window.innerHeight - dialog.height()) / 2;
        dialog.css("margin-top", e + "px");
        var i = $(".form");
        i.css("margin-top", ($(".modal-tab-2").height() - i.height()) / 2 + "px")
    }), $(".close").click(function() {
        tutorialModal.hide()
    });
    var accept = !1,
