$(document).on('click', '[data-toggle="lightbox"]', function(event) {
    event.preventDefault();
    $(this).ekkoLightbox({
        alwaysShowClose: true,
        onContentLoaded: function() {
            if ($(event.target).data('type') === 'youtube') {
                $('.ekko-lightbox .modal-dialog').css('max-width', '70vw');
            }
        }
    });
});

var eImgItemsContainer = $('.events__items'),
    eLayout = $('.events__items-layout').width(),
    eSlider = $('.events__items-container'),
    eImgWidth = eLayout / 4;

eImgItemsContainer.each(function() {
    var eImgItems = $(this).find('.events__item').length;
    $(this).css('width', eImgWidth * eImgItems + 'px');
});


eSlider.find('.events__arrow-left').on('click', moveThingLeft);
eSlider.find('.events__arrow-right').on('click', moveThingRight);

function moveThingLeft(e) {
    var parent = $(this).parent(),
        cur = parent.data('current') || 0;

    if (cur === 0) return;

    cur--;
    parent.data('current', cur);
    parent.find('.events__items').css('transform', 'translateX(' + -cur * eImgWidth + 'px' + ')');
}

function moveThingRight(e) {
    var parent = $(this).parent(),
        cur = parent.data('current') || 0;
        items = parent.find('.events__item').length;

    if (cur === items - 4) return;

    cur++;
    parent.data('current', cur);
    parent.find('.events__items').css('transform', 'translateX(' + -cur * eImgWidth + 'px' + ')');
}
</script>
